// SPDX-License-Identifier: BSD-3-Clause
/// @title ERC-20 implementation for Dexhune
// Sources: 
// https://eips.ethereum.org/EIPS/eip-20
// https://docs.google.com/document/d/1YLPtQxZu1UAvO9cZ1O2RPXBbT0mooh4DYKjA_jp-RLM
// https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/token/ERC20/IERC20.sol
/*
*    ........................................................
*    .%%%%%...%%%%%%..%%..%%..%%..%%..%%..%%..%%..%%..%%%%%%.
*    .%%..%%..%%.......%%%%...%%..%%..%%..%%..%%%.%%..%%.....
*    .%%..%%..%%%%......%%....%%%%%%..%%..%%..%%.%%%..%%%%...
*    .%%..%%..%%.......%%%%...%%..%%..%%..%%..%%..%%..%%.....
*    .%%%%%...%%%%%%..%%..%%..%%..%%...%%%%...%%..%%..%%%%%%.
*    ........................................................
*/

pragma solidity ^0.8.22;
import "./DexhuneERC20Base.sol";

error InsufficientBalance();
error DuplicateTransferAddress();
error InvalidTransferAddress();
error NotEnoughAllowance(uint256 allowance, uint256 expectedAllowance);

contract DexhuneERC20 is DexhuneERC20Base {
    string constant NAME = "Dexhune";
    string constant SYMBOL = "DXH";
    uint8 constant DECIMALS = 0;

    
    mapping(address => mapping(address => uint256)) private allowances;
    mapping(address => uint) private cooler;

    function name() public pure returns (string memory) {
        return NAME;
    } 

    function symbol() public pure returns (string memory) {
        return SYMBOL;
    } 

    function decimals() public pure returns (uint8) {
        return DECIMALS;
    }

    function totalSupply() public view returns (uint256) {
        return uint128(_supply);
    }

    function balanceOf(address owner) public view returns (uint256 balance) {
        return _getFullBalance(owner);
    }

    function transfer(address _to, uint256 _value) external returns (bool) {
        _transferInternal(msg.sender, _to, _value);
        return true;
    }

    function transferFrom(address from, address to, uint256 value) external returns (bool) {
        uint256 currentAllowance = allowances[from][msg.sender];

        if (value > currentAllowance) {
            revert NotEnoughAllowance(value, currentAllowance);
        }

        currentAllowance -= value;
        allowances[from][msg.sender] = currentAllowance;
        
        _transferInternal(from, to, value);
        

        return true;
    }

    function _transferInternal(address from, address to, uint256 value) private {
        uint256 balance = _getFullBalance(from);

        if (value > balance) {
            revert InsufficientBalance();
        }

        if (from == to) {
            revert DuplicateTransferAddress();
        }

        if (from == address(this)) {
            revert InvalidTransferAddress();
        }
        else if (to == address(this)) {
            revert InvalidTransferAddress();
        }

        balance -= value;

        uint256 targetBalance = _getFullBalance(to);
        targetBalance += value;

        _setBalance(from, balance);
        _setBalance(to, targetBalance);

        emit Transfer(from, to, value);
    }

    function approve(address _spender, uint256 _amount) external returns (bool) {
        allowances[msg.sender][_spender] = _amount;
        emit Approval(msg.sender, _spender, _amount);
        
        return true;
    }

    function allowance(address _owner, address _spender) public view returns (uint256 remaining) {
        return allowances[_owner][_spender];
    }
}

contracts/DexhuneERC20Base.sol.content:
// SPDX-License-Identifier: BSD-3-Clause
/// @title Dexhune ERC20 Root Implementation
// Sources: 
// https://github.com/OpenZeppelin/openzeppelin-contracts/blob/master/contracts/access/Ownable.sol
/*
*    ........................................................
*    .%%%%%...%%%%%%..%%..%%..%%..%%..%%..%%..%%..%%..%%%%%%.
*    .%%..%%..%%.......%%%%...%%..%%..%%..%%..%%%.%%..%%.....
*    .%%..%%..%%%%......%%....%%%%%%..%%..%%..%%.%%%..%%%%...
*    .%%..%%..%%.......%%%%...%%..%%..%%..%%..%%..%%..%%.....
*    .%%%%%...%%%%%%..%%..%%..%%..%%...%%%%...%%..%%..%%%%%%.
*    ........................................................
*/

pragma solidity ^0.8.22;

import "./interfaces/IERC20.sol";

abstract contract DexhuneERC20Base is IERC20 {
    address private _owner;

    address private _exchangeAddress = address(0);
    address private _daoAddress = address(0);
    uint256 private _totalMints;

    address[] private _holders;
    mapping(address => int128) private _balances;

    int128 _supply = 0;
    int16 _mintCount = 0;

    uint256 _nextMintTime = 0;
    uint256 _nextExchangeMintTime = 0;
    uint256 _nextDaoMintTime = 0;

    uint32 private constant MINT_INTERVAL = 4 days;
    uint32 private constant EXCHANGE_MINT_INTERVAL = 1 days;
    uint32 private constant DAO_MINT_INTERVAL = 1 days;

    int16 private constant MINT_LIMIT = 3650;
    
    /// @dev Mint value to add to supply divided by ten thousand. Default value is 12, indicating 0.12% [0.0012 * 10000]
    int8 private constant MINT_VALUE_PER_TEN_THOUSAND = 12;

    int32 private constant INITIAL_MINT_VALUE = 10_000_000;
    int32 private constant EXCHANGE_MINT_VALUE = 300_000;
    int32 private constant DAO_MINT_VALUE = 5760;

    address private constant DEAD_OWNER_ADDRESS = 0x000000000000000000000000000000000000dEaD;
    

    error UnauthorizedAccount(address account);

    
    error MintedTooEarly(uint256 timeRemaining);
    error MintLimitReached();
    error InvalidBalance(address addr, int128 balance);
    error ExchangeAddressNotSet();
    error PriceDaoAddressNotSet();

    
    constructor() {
        _owner = msg.sender;

        _supply = INITIAL_MINT_VALUE;
        _addToBalance(_owner, _supply);
    }

    modifier ownerOnly() {
        _ensureOwnership();
        _;
    }

    function _ensureOwnership() private view {
        if (msg.sender != _owner) {
            revert UnauthorizedAccount(msg.sender);
        }
    }

    function setDaoAddress(address addr) external ownerOnly {
        _daoAddress = addr;

        if (_exchangeAddress != address(0)) {
            _renounceContract();
        }
    }

    function setExchangeAddress(address addr) external ownerOnly {
        _exchangeAddress = addr;

        if (_daoAddress != address(0)) {
            _renounceContract();
        }
    }

    function getOwner() external view returns(address) {
        return _owner;
    }

    function mintingStartsAfter() external view returns (uint256) {
        uint256 timestamp = block.timestamp;

        if (_nextMintTime < timestamp) {
            return 0;
        }

        return _nextMintTime - timestamp;
    }

    function exchangeMintingStartsAfter() external view returns (uint256) {
        uint256 timestamp = block.timestamp;

        if (_nextExchangeMintTime < timestamp) {
            return 0;
        }

        return _nextExchangeMintTime - timestamp;
    }

    function daoMintingStartsAfter() external view returns (uint256) {
        uint256 timestamp = block.timestamp;

        if (_nextDaoMintTime < timestamp) {
            return 0;
        }

        return _nextDaoMintTime - timestamp;
    }

    function mint() external {
        if (_mintCount >= MINT_LIMIT) {
            revert MintLimitReached();
        }

        if (block.timestamp < _nextMintTime) {
            revert MintedTooEarly(block.timestamp - _nextMintTime);
        }

        _mintCount++;
        _nextMintTime = block.timestamp + MINT_INTERVAL;

        int128 supply = _supply;
        int128 delegated = (supply * MINT_VALUE_PER_TEN_THOUSAND) / 10000;
        
        int128 distributed = _distribute(delegated);
        int128 rem = delegated - distributed;

        if (rem > 0) {
            if (_exchangeAddress != address(0)) {
                _addToBalance(_exchangeAddress, rem);
            } else if (_daoAddress != address(0)) {
                _addToBalance(_daoAddress, rem);
            } else {
                _addToBalance(_owner, rem);
            }
        }

        _supply += delegated;
    }


    function mintToExchange() external {
        if (_nextExchangeMintTime > block.timestamp) {
            revert MintedTooEarly(_nextExchangeMintTime - block.timestamp);
        }

        if (_exchangeAddress == address(0)) {
            revert ExchangeAddressNotSet();
        }

        _nextExchangeMintTime = block.timestamp + EXCHANGE_MINT_INTERVAL;
        _addToBalance(_exchangeAddress, EXCHANGE_MINT_VALUE);
    }

    function mintToDao() external {
        if (block.timestamp < _nextDaoMintTime) {
            revert MintedTooEarly(block.timestamp - _nextDaoMintTime);
        }

        if (_daoAddress == address(0)) {
            revert PriceDaoAddressNotSet();
        }

        _nextDaoMintTime = block.timestamp + DAO_MINT_INTERVAL;
        _addToBalance(_daoAddress, DAO_MINT_VALUE);
    }

    function _renounceContract() private {
        _owner = DEAD_OWNER_ADDRESS;
    }

    function _getBalance(address addr) internal view returns(int128) {
        int128 balance = _balances[addr];

        if (balance < 0) {
            balance = 0;
        }

        return balance;
    }

    function _getFullBalance(address addr) internal view returns(uint256) {
        return uint128(_getBalance(addr));
    }

    function _setBalance(address addr, int128 balance) private {
        if (balance < 0) {
            revert InvalidBalance(addr, balance);
        }

        if (balance == 0) {
            balance = -1;
        }

        int128 oldBalance = _balances[addr];

        // User does not have a balance, no further changes required
        if (oldBalance == 0 && balance == -1) {
            return;
        }

        // Balance is zero, user does not exist
        if (oldBalance == 0) {
            _holders.push(addr);
        }

        _balances[addr] = balance;
    }

    function _setBalance(address addr, uint256 balance) internal {
        _setBalance(addr, int128(uint128(balance)));
    }

    function _addToBalance(address addr, int128 value) private {
        int128 balance = _balances[addr];

        // Balance is zero, user does not exist
        if (balance == 0) {
            _holders.push(addr);
        }

        balance += value;
        _balances[addr] = balance;

        emit Transfer(address(this), addr, uint128(value));
    }

    function _distribute(int128 funds) private returns (int128) {
        address holder;
        int128 balance;

        int128 cut;
        int128 distributed;

        for (uint i = 0; i < _holders.length; i++) {
            holder = _holders[i];
            balance = _balances[holder];
        
            cut = (balance * funds) / _supply;
            
            balance += cut;
            distributed += cut;

            _balances[holder] = balance;

            if (cut > 0) {
                emit Transfer(address(this), holder, uint128(cut));
            }
        }

        return distributed;
    }
}

contracts/DexhuneExchange.sol.content:
// SPDX-License-Identifier: BSD-3-Clause
/// @title Dexhune Exchange implementation
/*
*    ........................................................
*    .%%%%%...%%%%%%..%%..%%..%%..%%..%%..%%..%%..%%..%%%%%%.
*    .%%..%%..%%.......%%%%...%%..%%..%%..%%..%%%.%%..%%.....
*    .%%..%%..%%%%......%%....%%%%%%..%%..%%..%%.%%%..%%%%...
*    .%%..%%..%%.......%%%%...%%..%%..%%..%%..%%..%%..%%.....
*    .%%%%%...%%%%%%..%%..%%..%%..%%...%%%%...%%..%%..%%%%%%.
*    ........................................................
*/

pragma solidity ^0.8.22;

import "./interfaces/IERC20.sol";
import "./interfaces/IPriceDAO.sol";
import "./DexhuneExchangeBase.sol";

contract DexhuneExchange is DexhuneExchangeBase {
    uint256 public listingCost = INITIAL_LISTING_PRICE;

    Token[] private _tokens;
    Order[] private _orders;

    Order[] public orders = _orders;

    mapping(address => uint256) private _tokenMap;
    mapping(address => uint256[]) private _ordersByToken;
    mapping(address => uint256[]) private _ordersByUser;

    mapping(address => uint256) _avaxBalance; // Balance X
    mapping(address => uint256) _allocBalances; // Balance Y
    mapping(address => uint256) _balances; // Balance Z
    
    uint256 private _price;
    uint256 private _lastPriceCheck;
    IPriceDAO private _oracle;

    uint8 private constant NATIVE_TOKEN_DECIMALS = 18;
    uint256 private constant MAX_ORDER_COUNT = 100_000;
    uint256 private constant MAX_TOKEN_COUNT = 1_000_000;
    uint256 private constant ORDER_LIFESPAN = 40 seconds;
    uint256 private constant PRICE_CHECK_INTERVAL = 4 minutes;

    uint256 private constant LISTING_PRICE_INCREASE_BY_THOUSAND = 5;
    uint256 private constant INITIAL_LISTING_PRICE = 1000; // DXH
    uint256 private constant MAX_LISTING_FEE = 1_000_000;

    uint256 private constant CLEAR_ORDERS_USER_LIMIT = 50;
    uint256 private constant CLEAR_ORDERS_LIMIT = 100;
    
    constructor() {
        owner = msg.sender;
    }

    function viewPrice() external view returns (uint256) {
        return _price;
    }

    function assignPriceDAO(address addr) external ownerOnly {
        _oracle = IPriceDAO(addr);
        _ensurePrice();

        emit AssignedPriceDAO(addr);
    }

    function viewToken(address tokenAddr) external view returns (TokenDataModel memory) {
        uint256 index = _tokenMap[tokenAddr];
        return _displayToken(index);
    }

    function viewTokenByIndex(uint256 tokenNo) external view returns (TokenDataModel memory) {
        return _displayToken(tokenNo);
    }

    function viewOrder(uint256 index) external view returns (Order memory) {
        if (index >= _orders.length) {
            revert OrderDoesNotExist();
        }

        return _orders[index];
    }

    function viewOrderByToken(address tokenAddr, uint256 index) external view returns (Order memory) {
        uint256[] storage indexes = _ordersByToken[tokenAddr];

        if (index >= indexes.length) {
            revert OrderDoesNotExist();
        }

        uint256 n = indexes[index];

        Order memory order = _orders[n];
        return order;
    }

    function getBalance() external view returns(uint256) {
        return address(this).balance;
    }

    function queryBalance(address tokenAddr, bool isAVAX) external view returns (uint256) {
        // Token memory token = _fetchToken(tokenAddr);

        if (isAVAX) {
            return _avaxBalance[tokenAddr];
        } else {
            return _balances[tokenAddr];
        }
    }

    function listToken(address tokenAddr, uint256 reward, uint256 rewardThreshold, string memory price) external {
        _listToken(tokenAddr, PricingScheme.Relative, reward, rewardThreshold, address(0), price);
    }

    function listParityToken(address tokenAddr, address parityAddr, uint256 reward, uint256 rewardThreshold, string memory price) external {
        _listToken(tokenAddr, PricingScheme.Parity, reward, rewardThreshold, parityAddr, price);
    }

    function _listToken(address tokenAddr, PricingScheme scheme, uint256 reward, uint256 rewardThreshold, address parityAddr, string memory price) private {
        if (_tokens.length == 0) {
            if (msg.sender != owner) {
                revert OnlyOwnerMustSetDefaultToken();
            }
        }

        address addr = tokenAddr;
        uint256 index = _tokenMap[addr];

        if (index != 0 && _tokens.length > 0) {
            revert TokenAlreadyExists(addr);
        }

        if (_tokens.length >= MAX_TOKEN_COUNT) {
            revert TokenLimitReached();
        }

        uint256 nPrice = 0;

        if (scheme == PricingScheme.Relative) {
            nPrice = _parseNumber(price);
        }

        _prepareToken(addr, nPrice, scheme, 
            parityAddr, reward, rewardThreshold);
    }
    
    function createBuyOrder(address tokenAddr) external payable {
        uint256 amount = msg.value;

        if (amount <= 0) {
            revert RejectedZeroAmount();
        }

        Token storage token = _fetchStorageToken(tokenAddr);
        _avaxBalance[tokenAddr] += amount;
        

        _createOrder(token, true, amount);
    }

    function createSellOrder(address tokenAddr, uint256 amount) external {
        if (amount <= 0) {
            revert RejectedZeroAmount();
        }

        Token storage token = _fetchStorageToken(tokenAddr);
        if (!_withdrawToken(token.instance, msg.sender, amount)) {
            revert DepositFailed();
        }
                
        _allocBalances[tokenAddr] += amount;
        _balances[tokenAddr] += amount;

        _createOrder(token, false, amount);
    }

    function deposit(address tokenAddr) external payable {
        _avaxBalance[tokenAddr] += msg.value;
    }

    function depositToken(address tokenAddr, uint256 amount) external {
        _depositToken(tokenAddr, msg.sender, amount);
    }

    function depositTokenFrom(address tokenAddr, address fromAddress, uint256 amount) external {
        _depositToken(tokenAddr, fromAddress, amount);
    }

    function takeBuyOrder(address tokenAddr, uint256 amount) external {
        Token memory token = _fetchToken(tokenAddr);

        Order storage order;
        uint256 orderIndex;
        bool success;

        (success, orderIndex) = _findSuitableOrder(tokenAddr, amount);

        if (!success) {
            revert NoSuitableOrderFound();
        }

        if (!_withdrawToken(token.instance, tokenAddr, amount)) {
            revert DepositFailed();
        }

        order = _orders[orderIndex];
        _takeOrder(order, true, orderIndex, amount, tokenAddr);
    }

    function takeSellOrder(address tokenAddr) external payable {
        uint256 amount = msg.value;

        Order storage order;
        uint256 orderIndex;
        bool success;

        (success, orderIndex) = _findSuitableOrder(tokenAddr, amount);

        if (!success) {
            revert NoSuitableOrderFound();
        }

        order = _orders[orderIndex];
        _takeOrder(order, false, orderIndex, amount, tokenAddr);
    }

    function updateTokenBalance(address tokenAddr) external {
        Token memory token = _fetchToken(tokenAddr);

        uint256 balance = token.instance.balanceOf(address(this));
        _balances[tokenAddr] = balance;
    }

    function _depositToken(address tokenAddr, address fromAddress, uint256 amount) private {
        Token memory token = _fetchToken(tokenAddr);

        if (!_withdrawToken(token.instance, fromAddress, amount)) {
            revert DepositFailed();
        }

        // token.yBalance += amount;
        _balances[tokenAddr] += amount;
    }

    function _findSuitableOrder(address tokenAddr, uint256 amount) private view returns (bool success, uint256 index) {
        Order memory order;
        uint256[] storage norders = _ordersByToken[tokenAddr];

        bool found;

        for (uint256 i = norders.length; i > 0 && !found; i--) {
            uint256 n = norders[i];
            order = _orders[n];

            if (order.pending == amount) {
                found = true;
                index = n;
            } else if (!found && order.pending >= amount) {
                found = true;
                index = n;
            }
        }

        return (found, index);
    }


    function _takeOrder(Order storage order, bool orderType, uint256 index, uint256 amt, address tokenAddr) private {
        Token memory token = _fetchToken(tokenAddr);

        uint256 amount; uint256 nAmount;
        amount = nAmount = amt;

        if (!orderType) { // Sells
            amount = _normalize(amt, token.dec);
        }

        order = _orders[index];
        
    
        uint256 price = order.price;
        uint256 principalDelta = order.principal;

        bool isPartial = principalDelta != amount;
        address payable makerAddr = order.makerAddr;
        
        if (isPartial) {
            if (orderType) {
                principalDelta = _mul(amount, price);
            } else {
                principalDelta = _div(amount, price);
            }
        }

        _makeOrderPayments(order, token, amount, principalDelta);
        uint256 tokenDelta = orderType ? amount : principalDelta;

        if (isPartial) {
            order.pending -= amount;
            order.principal -= principalDelta;
        } else {
            _removeItem(_orders, index);
        }

        uint256 reward = token.reward;
        bool canReward = reward > 0 && tokenDelta >= token.rewardThreshold;

        if (canReward) {
            uint256 alloc = _allocBalances[token.addr];
            uint256 balance = _balances[token.addr];
            uint256 rem = balance - alloc;

            if (rem > reward) {
                if (!_sendToken(token, msg.sender, reward)) {
                    revert FailedToSendReward();
                }   
            }
        }

        emit OrderTaken(index, orderType, makerAddr, 
            msg.sender, token.addr, nAmount, isPartial);
    }

    function _makeOrderPayments(Order memory order, Token memory token, uint256 amount, uint256 principal) private {
        uint256 nativeDelta;
        uint256 tokenDelta;
        address payable avaxAddr;
        address payable tkAddr;

        if (order.orderType) {
            nativeDelta = principal;
            tokenDelta = amount;

            avaxAddr = order.makerAddr;
            tkAddr = payable(msg.sender);
        } else {
            nativeDelta = amount;
            tokenDelta = principal;

            avaxAddr = payable(msg.sender);
            tkAddr = order.makerAddr;
        }

        // TODO: Add reward to the delta
        if (!_sendToken(token, tkAddr, tokenDelta)) {
            revert FailedToTakeOrder();
        }

        if (!_sendAVAX(avaxAddr, nativeDelta)) {
            revert FailedToTakeOrder();
        }

        // TODO: Balances should all be sufficient
        _avaxBalance[token.addr] -= nativeDelta;
        bool canUseAlloc = _allocBalances[token.addr] > tokenDelta;

        if (canUseAlloc) {
            _allocBalances[token.addr] -= tokenDelta;
        }

        _balances[token.addr] -= tokenDelta;
    }

    function settleOrders(address tokenAddr, bool orderType) external {
        Token memory token = _fetchToken(tokenAddr);

        uint256[] storage norders = _ordersByToken[token.addr];
        Order storage order;

        bool settled;
        uint256 balance = orderType ? _balances[tokenAddr] : _avaxBalance[tokenAddr];
        
        // TODO: Retest this loop
        for (uint i = norders.length; balance > 0 && i > 0; i--) {
            uint256 n = norders[i];
            order = _orders[n];

            if (order.orderType != orderType) {
                continue;
            }

            if (orderType) {
                (settled, balance) = _settleBuyOrder(token, order, i, balance);
            } else {
                (settled, balance) = _settleSellOrder(order, balance, i);
            }

            if (!settled) {
                continue;
            }
            
            _removeItem(norders, i);
            _removeItem(_orders, n);
        }

        // TODO: Decimals for balances might need to be normalized
        if (orderType) {
            // token.yBalance = balance;
            _balances[token.addr] = balance;
        } else {
            _avaxBalance[token.addr] = balance;
            // token.xBalance = balance;
        }
    }

    function clearOrders() external {
        uint256[] storage uorders = _ordersByUser[msg.sender];
        bool userOnly = uorders.length > 0;

        uint256 reverted;
        uint256 limit = userOnly ? CLEAR_ORDERS_USER_LIMIT : CLEAR_ORDERS_LIMIT;
        
        uint256 n;
        uint256 timestamp = block.timestamp;
        Order memory order;

        if (_orders.length <= 0) {
            return;
        }
        

        if (userOnly) {
            for (uint256 i = uorders.length; i > 0 && reverted <= limit; i--) {
                n = uorders[i];
                order = _orders[n];
                
                if (timestamp - order.created > ORDER_LIFESPAN) {
                    if (_revertOrder(order, n)) {
                        _removeItem(uorders, i);
                        reverted++;
                    }
                }
            }

            return;
        }

        for (uint256 i = _orders.length; i > 0 && reverted <= limit; i--) {
            order = _orders[i];

            if (timestamp - order.created > ORDER_LIFESPAN) {
                if (_revertOrder(order, n)) {
                    reverted++;
                }
            }
        }
    }

    function _revertOrder(Order memory order, uint256 index) private returns (bool) {
        Token memory token = _fetchToken(order.tokenAddr);

        if (order.orderType) { // Buy
            if (!_sendAVAX(order.makerAddr, order.principal)) {
                return false;
            }

            // token.xBalance -= order.principal;
            _avaxBalance[token.addr] -= order.principal;
        } else {
            if (!_sendToken(token, order.makerAddr, order.principal)) {
                return false;
            }

            // token.yBalance -= order.principal;
            _allocBalances[token.addr] -= order.principal;
            _balances[token.addr] -= order.principal;
        }

        // TODO: Review Order events
        emit OrderReverted(index, order.orderType, order.makerAddr);
        _removeItem(_orders, index);
        


        return true;
    }

    function _createOrder(Token storage token, bool orderType, uint256 amount) private {
        uint256 price;

        if (token.scheme == PricingScheme.Relative) {
            _ensurePrice();
            price = _div(_price, token.price);

        } else if (token.scheme == PricingScheme.Parity) {
            bool canUpdate = token.lastPriceCheck == 0 || block.timestamp - token.lastPriceCheck >= PRICE_CHECK_INTERVAL;

            if (canUpdate) {
                Token memory dxh = _tokens[0];
                IERC20 tokenInst = token.instance;
                IERC20 dxhInst = dxh.instance;


                uint256 tBalance = tokenInst.balanceOf(token.parityAddr);
                uint256 dxhBalance = dxhInst.balanceOf(token.parityAddr);

                tBalance = _normalize(tBalance, token.dec);
                dxhBalance = _normalize(dxhBalance, dxh.dec);

                token.price = price = _div(dxhBalance, tBalance);
                token.lastPriceCheck = block.timestamp;
            } else {
                price = token.price;
            }
        }

        uint256 pending;

        // uint256 nPrice = _normalize(price, NATIVE_TOKEN_DECIMALS);
        // 1 AVAX = 20tk
        // 60tk = 60/20 * 1 = 3 AVAX

        if (orderType) {
            // Buy
            pending = _div(_normalize(amount, token.dec), price);
        } else {
            pending = _mul(_normalize(amount, token.dec), price);
        }


        uint reward;

        if (price >= token.rewardThreshold) {
            reward = token.reward;
        }

        // pending = _normalize(pending, NATIVE_TOKEN_DECIMALS);

        Order memory order = Order(payable(msg.sender), token.addr, orderType, block.timestamp, reward, price, amount, pending);
        _orders.push(order);

        uint256 index = _orders.length - 1;
        _ordersByUser[msg.sender].push(index);
        _ordersByToken[token.addr].push(index);

        emit OrderCreated(index, orderType, token.addr, amount, price);
    }

    
    function _prepareToken(address addr, uint256 price, PricingScheme scheme, address parityAddr, uint256 reward, uint256 rewardThreshold) private {
        IERC20 inst = IERC20(addr);
        
        string memory name;
        string memory sym;
        uint256 scalar;
        uint256 balance;
        uint8 decimals;

        try inst.balanceOf(address(this)) returns (uint256 _bal) {
            balance = _bal;

            try inst.decimals() returns (uint8 _dec) {
                decimals = _dec;
            } catch {
                decimals = NATIVE_TOKEN_DECIMALS;
            }

            if (decimals > NATIVE_TOKEN_DECIMALS) {
                revert TokenNotSupported_TooManyDecimals();
            }

            scalar = _computeScalar(decimals);

            try inst.name() returns (string memory _name) {
                name = _name;
            } catch {}

            try inst.symbol() returns (string memory _sym) {
                sym = _sym;
            } catch {}
        } catch {
            revert InvalidTokenContract();
        }

        if (parityAddr != address(0) && price > 0) {
            revert ParityShouldNotHavePrice();
        }

        Token memory token = Token(
            name, sym, decimals, scalar, 
            addr, parityAddr,
            scheme, reward, rewardThreshold,
            price, 0, inst
        );

        _tokens.push(token);
        _tokenMap[token.addr] = _tokens.length;
        _balances[token.addr] = balance;

        emit TokenListed(token.name, token.sym, token.scheme, token.addr, _tokens.length);
    }

    function _settleBuyOrder(Token memory token, Order storage order, uint256 index, uint256 balance) private returns (bool settled, uint256 remBalance) {
        uint256 amount;
        bool isPartial = balance < order.pending;
        
        if (isPartial) {
            amount = balance;
        } else {
            amount = order.pending;
        }

        if (_sendToken(token, order.makerAddr, amount)) {
            balance -= amount;

            if (isPartial) {
                order.pending -= amount;
                order.principal = _mul(order.pending, order.price);
            }
        } else {
            return (false, balance);
        }

        emit OrderSettled(index, order.orderType, order.makerAddr, isPartial);

        return (!isPartial, balance);
    }

    function _settleSellOrder(Order storage order, uint256 balance, uint256 index) private returns (bool settled, uint256 remBalance) {
        uint256 amount;
        bool isPartial = balance < order.pending;

        if (isPartial) {
            amount = balance;
        } else {
            amount = order.pending;
        }

        if (_sendAVAX(order.makerAddr, amount)) {
            balance -= amount;

            if (isPartial) {
                order.pending -= amount;
                order.principal = _div(order.pending, order.price);
            }

            emit OrderSettled(index, order.orderType, order.makerAddr, isPartial);

            return (!isPartial, balance);
        }
        
        return (false, balance);
    }

    function _chargeForListing() private {
        if (_tokens.length == 0) {
            return;
        }

         Token memory def = _tokens[0];
        
        if (!_withdrawToken(def.instance, msg.sender, listingCost)) {
            revert InsufficientBalanceForListing(listingCost);
        }

        // def.yBalance += listingCost;
        _balances[def.addr] += listingCost;
        
        uint256 lPrice = (listingCost * LISTING_PRICE_INCREASE_BY_THOUSAND) / 1000;
        listingCost += lPrice;
    }

    function _ensurePrice() private {
        if (_lastPriceCheck != 0 && block.timestamp - _lastPriceCheck < PRICE_CHECK_INTERVAL) {
            return;
        }

        string memory szPrice = _oracle.getPrice();

        (uint256 price, bool success) = _tryParseNumber(szPrice);

        if (success) {
            _price = price;
        }

        _lastPriceCheck = block.timestamp;   
    }

    function _fetchToken(address addr) private view returns(Token memory) {
        uint256 index = _tokenMap[addr];

        if (index == 0 || index > _tokens.length) {
            revert TokenNotListed();
        }

        return _tokens[index - 1];
    }

    function _fetchStorageToken(address addr) private view returns(Token storage) {
        uint256 index = _tokenMap[addr];

        if (index == 0 || index > _tokens.length) {
            revert TokenNotListed();
        }

        return _tokens[index - 1];
    }

    function _displayToken(uint256 index) private view returns(TokenDataModel memory) {
        if (index == 0 || index > _tokens.length) {
            revert TokenNotListed();
        }

        Token memory token = _tokens[index - 1];
        uint256 orderCount = _ordersByToken[token.addr].length;

        return TokenDataModel(
            index, token.name, token.sym, token.addr, 
            token.parityAddr, token.reward, 
            token.rewardThreshold, token.scheme, token.price, orderCount);
    }
}

contracts/DexhuneExchangeBase.sol.content:
// SPDX-License-Identifier: BSD-3-Clause
/// @title Base objects for Dexhune Exchange
/*
*    ........................................................
*    .%%%%%...%%%%%%..%%..%%..%%..%%..%%..%%..%%..%%..%%%%%%.
*    .%%..%%..%%.......%%%%...%%..%%..%%..%%..%%%.%%..%%.....
*    .%%..%%..%%%%......%%....%%%%%%..%%..%%..%%.%%%..%%%%...
*    .%%..%%..%%.......%%%%...%%..%%..%%..%%..%%..%%..%%.....
*    .%%%%%...%%%%%%..%%..%%..%%..%%...%%%%...%%..%%..%%%%%%.
*    ........................................................
*/

pragma solidity ^0.8.22;
import "./utils/Ownable.sol";
import "./interfaces/IERC20.sol";
import "./interfaces/IPriceDAO.sol";
import "./libraries/DexhuneMath.sol";

abstract contract DexhuneExchangeBase is Ownable {
    /////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    // Transfers
    function _sendToken(Token memory token, address targetAddr, uint256 amount) internal returns (bool) {
        try token.instance.transfer(targetAddr, amount) returns (bool success) {
            if (success) {
                emit TokenTransferred(amount, targetAddr, token.addr);
            }
            return success;
        } catch {
            return false;
        }
    }

    function _withdrawToken(IERC20 token, address targetAddr, uint256 amount) internal returns (bool) {
        try token.transferFrom(targetAddr, address(this), amount) returns (bool success) {
            return success;
        } catch {
            return false;
        }
    }

    function _sendAVAX(address payable to, uint256 amount) internal returns(bool) {
        (bool sent, ) = to.call{value: amount}("");

        if (sent) {
            emit AVAXTransferred(amount, to);
        }

        return sent;
    }
    /////////////////////////////////////////////////////////////////////////////////////////////////////////////////

    /////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    // Math
    function _mul(uint256 num1, uint256 num2) internal pure returns(uint256) {
        return DexhuneMath.mul(num1, num2);
        // return 0;
    }

    function _div(uint256 num1, uint256 num2) internal pure returns(uint256) {
        return DexhuneMath.div(num1, num2);
        // return 0;
    }

    function _parseNumber(string memory value) internal pure returns(uint256) {
        (uint256 num, bool success) = _tryParseNumber(value);

        if (!success) {
            revert FailedStringToNumberConversion();
        }

        return num;
    }

    function _tryParseNumber(string memory value) internal pure returns(uint256, bool) {
        uint256 xres; uint256 res;
        bytes memory buffer = bytes(value);
        uint8 b;
        
        bool hasErr;
        bool hasDec;
        uint8 dec = 0;
    
        for (uint i = 0; i < buffer.length && !hasErr; i++) {
            b = uint8(buffer[i]);
            
            if (b >= 48 && b <= 57) {
                xres = res;

                if (hasDec) {
                    dec--;
                } else {
                    res *= 10;
                }

                res += (b - 48) * 10 ** dec;

                if (xres > res) {
                    // Detect overflow
                    hasErr = true;
                }
            } else if (b == 46) {
                if (hasDec) {
                    hasErr = true;
                }

                hasDec = true;
                dec = 18;
                res *= 10 ** dec;
            }
        }

        if (!hasDec) {
            res *= 1e18;
        }

        return (res, !hasErr);
    }   
    /////////////////////////////////////////////////////////////////////////////////////////////////////////////////

    /////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    // Utils
    function _removeItem(uint256[] storage arr, uint256 index) internal {
        uint256 last = arr.length - 1;
        
        if (index != last) {
            arr[index] = arr[last];
        }

        arr.pop();
    }

    function _removeItem(Order[] storage arr, uint256 index) internal {
        uint256 last = arr.length - 1;

        if (index != last) {
            arr[index] = arr[last];
        }

        arr.pop();
    }

    function _scaleUp(uint256 amount, uint256 decimals) internal pure returns(uint256) {
        // TODO: Add this to a mapping
        return decimals == 1 ? amount : amount *  10 ** decimals;
    }

    function _scaleDown(uint256 amount, uint256 decimals) internal pure returns(uint256) {
        // TODO: Add this to a mapping
        return decimals == 1 ? amount : amount / 10 ** decimals;
    }



    function _normalize(uint256 amount, uint256 decimals) internal pure returns(uint256) {
        return decimals == 1 ? amount : amount * _computeScalar(decimals);
    }

    function _denormalize(uint256 amount, uint256 decimals) internal pure returns(uint256) {
        return decimals == 1 ? amount : amount / _computeScalar(decimals);
    }

    function _computeScalar(uint256 decimals) internal pure returns(uint256 scalar) {
        if (decimals == 18) {
            scalar = 1;
        } else {
            unchecked {
                scalar = 10 ** (18 - decimals);
            }
        }
    }
    /////////////////////////////////////////////////////////////////////////////////////////////////////////////////
    

    enum PricingScheme {
        Relative,
        Parity
    }

    struct Token {
        string name;
        string sym;
        uint8 dec;
        uint256 scalar;
        address addr;
        address parityAddr;
        
        PricingScheme scheme;

        uint256 reward;
        uint256 rewardThreshold;

        
        // uint256 xBalance; // Native Balance (AVAX)
        // uint256 yBalance; // Token Balance

        uint256 price;
        uint256 lastPriceCheck;

        IERC20 instance;
    }

    struct LiquidToken {
        uint256 xBalance; // Native Balance (AVAX)
        uint256 yBalance; // Token Balance
        uint256 lastPriceCheck;
    }

    struct TokenDataModel {
        uint256 tokenNo;
        string name;
        string sym;
        address addr;
        address parityAddr;
        uint256 reward;
        uint256 rewardThreshold;
        PricingScheme scheme;
        uint256 price;

        uint256 orders;
    }

    struct Order {
        address payable makerAddr;
        address tokenAddr;
        bool orderType;

        uint256 created;
        uint256 rewardAmount;

        uint256 price;
        uint256 principal;
        uint256 pending;
    }

    error FailedStringToNumberConversion();
    error TokenAlreadyExists(address contractAddr);
    error TokenLimitReached();
    error OrderLimitReachedTryLater();
    error TokenNotListed();
    error OrderDoesNotExist();
    error InvalidTokenContract();
    error ParityShouldNotHavePrice();
    error InsufficientBalance(uint256 balance);
    error OnlyOwnerMustSetDefaultToken();
    error TokenNotSupported_TooManyDecimals();
    error DepositFailed();
    error NoSuitableOrderFound();
    error FailedToTakeOrder();
    error FailedToSendReward();
    error InsufficientBalanceForListing(uint256 listingPrice);
    error RejectedZeroAmount();

    
    event AssignedPriceDAO(address addr);
    event TokenListed(string indexed name, string indexed symbol, PricingScheme pricingScheme, address addr, uint256 index);
    event OrderCreated(uint256 index, bool orderType, address tokenAddr, uint256 amount, uint256 price);
    event OrderTaken(uint256 index, bool orderType, address maker, address taker, address token, uint256 amount, bool isPartial);
    event OrderReverted(uint256 index, bool orderType, address maker);
    event OrderSettled(uint256 index, bool orderType, address maker, bool isPartial);
    
    event AVAXTransferred(uint256 amount, address targetAddr);
    event TokenTransferred(uint256 amount, address targetAddr, address tokenAddr);
    
}

contracts/DexhunePriceDAO.sol.content:
// SPDX-License-Identifier: BSD-3-Clause
/// @title Dexhune Oracle/Price DAO Logic
/*
*    ........................................................
*    .%%%%%...%%%%%%..%%..%%..%%..%%..%%..%%..%%..%%..%%%%%%.
*    .%%..%%..%%.......%%%%...%%..%%..%%..%%..%%%.%%..%%.....
*    .%%..%%..%%%%......%%....%%%%%%..%%..%%..%%.%%%..%%%%...
*    .%%..%%..%%.......%%%%...%%..%%..%%..%%..%%..%%..%%.....
*    .%%%%%...%%%%%%..%%..%%..%%..%%...%%%%...%%..%%..%%%%%%.
*    ........................................................
*/

pragma solidity ^0.8.22;

import "./interfaces/IERC20.sol";
import "./DexhunePriceDAOBase.sol";

contract DexhunePriceDAO is DexhunePriceDAOBase {    
    string private _price;
    uint256 private _deadline;
    uint256 private _votingDeadline;
    PriceProposal private _proposal;

    address[] private _voters;
    mapping(address => int16) private _votes;
    
    uint256 internal constant PROPOSAL_DURATION = 10 minutes;
    uint256 internal constant PROPOSAL_VOTING_DURATION = 5 minutes;

    uint256 private constant MAX_REWARD = 20_000;
    uint256 private constant REWARD_MULTIPLIER = 20;

    constructor() {
        owner = msg.sender;
        _proposal = PriceProposal(owner, "NULL", "NULL", true);
    }

    function getPrice() public view returns(string memory) {
        return _price;
    }

    function latestProposal() public view returns (PriceProposal memory) {
        return _proposal;
    }

    function votingEndsAfter() public view returns (uint256) {
        uint256 timestamp = block.timestamp;

        if (_votingDeadline < timestamp) {
            return 0;
        }
        
        return _votingDeadline - timestamp;
    }

    function proposalEndsAfter() public view returns (uint256) {
        uint256 timestamp = block.timestamp;

        if (_deadline < timestamp) {
            return 0;
        }

        return _deadline - timestamp;
    }

    function proposePrice(string memory price, string memory description) external {        
        if (_getHolderBalance() <= 0) {
            revert NotEligible();
        }

        if (!_proposal.finalized) {
            if (_deadline > block.timestamp) {
                revert ProposalIsStillActive();
            }
        }

        _deadline = block.timestamp + PROPOSAL_DURATION;
        _votingDeadline = block.timestamp + PROPOSAL_VOTING_DURATION;
        
        _proposal = PriceProposal(msg.sender, price, description, false);
        
        emit ProposalCreated(price, description, msg.sender);
    }

    function voteUp() external {
        uint16 balance = _checkVotes();
        address voter = msg.sender;

        _voters.push(voter);
        _votes[voter] = int16(balance);
        emit VotedUp(voter, balance);
    }

    function voteDown() external {
        uint16 balance = _checkVotes();
        address voter = msg.sender;
        
        _voters.push(voter);
        _votes[voter] = -int16(balance);
        emit VotedDown(voter, balance);
    }

    function finalizeProposal() external {
        if (_votingDeadline > block.timestamp) {
            revert ProposalIsStillActive();
        } else if (block.timestamp >= _deadline) {
            revert ProposalHasExpired();
        } else if (_proposal.finalized) {
            revert ProposalAlreadyFinalized();
        }

        

        uint16 up = 0;
        uint16 down = 0;
        
        int16 value;
        uint16 balance;

        address voterAddr;

        for (uint256 i = _voters.length; i > 0; i--) {
            voterAddr = _voters[i - 1];
            value = _votes[voterAddr];
            
            unchecked {
                balance = uint16(_getHolderBalance(voterAddr));
            }

            if (_abs(value) != balance) {
                continue;
            }

            if (value > 0) {
                up += uint16(value);
            } else if (value < 0) {
                down += uint16(-value);
            }
        }


        _proposal.finalized = true;

        if (up > down) {
            _rewardProposer(up + down);
            _price = _proposal.value;

            emit ProposalPassed(up, down, _price);
            
        } else {
            emit ProposalDenied(up, down);
        }
    }

    function _checkVotes() private view returns (uint16) {
        if (block.timestamp >= _votingDeadline) {
            revert NoActiveProposal();
        }

        int16 value = _votes[msg.sender];

        if (value != 0) {
            revert AlreadyVoted();
        }

        // Overflow if balance is greater than 1k (Only 1k Peng NFTs should exist)
        uint16 balance = uint16(_getHolderBalance());
        
        if (balance <= 0) {
            revert NotEligible();
        }

        return balance;
    }

    function _rewardProposer(uint16 total) private {
        address addr = _proposal.proposerAddr;
        uint256 reward = total * REWARD_MULTIPLIER;

        if (reward > MAX_REWARD) {
            reward = MAX_REWARD;
        }

        try _token.transfer(addr, reward) {
            emit RewardedProposer(addr, reward);
        } catch {}
    }
}

contracts/DexhunePriceDAOBase.sol.content:
// SPDX-License-Identifier: BSD-3-Clause
/// @title Base objects for Dexhune Price Dao
/*
*    ........................................................
*    .%%%%%...%%%%%%..%%..%%..%%..%%..%%..%%..%%..%%..%%%%%%.
*    .%%..%%..%%.......%%%%...%%..%%..%%..%%..%%%.%%..%%.....
*    .%%..%%..%%%%......%%....%%%%%%..%%..%%..%%.%%%..%%%%...
*    .%%..%%..%%.......%%%%...%%..%%..%%..%%..%%..%%..%%.....
*    .%%%%%...%%%%%%..%%..%%..%%..%%...%%%%...%%..%%..%%%%%%.
*    ........................................................
*/

pragma solidity ^0.8.22;

import "./utils/Ownable.sol";
import "./interfaces/IERC20.sol";
import "./interfaces/IERC721.sol";

abstract contract DexhunePriceDAOBase is Ownable {
    IERC20 internal _token;
    IERC721 internal _nft;

    address public nftAddress;
    address public tokenAddress;

    function assignNFTCollection(address addr) external ownerOnly {
        IERC721 nft = IERC721(addr);
        nft.balanceOf(address(this));
        
        _nft = nft;
        nftAddress = addr;

        emit AssignedNFTCollection(addr);
    }

    function assignTokenAddress(address addr) external ownerOnly {
        IERC20 token = IERC20(addr);
        token.balanceOf(address(this));

        _token = token;
        tokenAddress = addr;

        emit AssignedToken(addr);
    }

    function _getHolderBalance() internal view returns (uint256) {
        return _nft.balanceOf(msg.sender);
    }

    function _getHolderBalance(address addr) internal view returns (uint256) {
        return _nft.balanceOf(addr);
    }

    function _abs(int16 x) internal pure returns (uint16) {
        return uint16(x >= 0 ? x : -x);
    }


    struct PriceProposal {
        address proposerAddr;
        string value;
        string description;        
        bool finalized;
    }

    /// @notice A NFT collection has been assigned
    /// @dev Assigns an NFT collection
    /// @param addr The contract address of the NFT collection
    event AssignedNFTCollection(address addr);

    /// @notice An ERC20 token has been assigned
    /// @dev Assigns an ERC20 token for internal transactions
    /// @param addr The contract address of the ERC20 token
    event AssignedToken(address addr);



    /// @notice A new proposal was created
    /// @dev Notifies that a new proposal was created
    /// @param price The proposed price
    /// @param description Description of the proposal
    /// @param proposer Address of the proposer
    event ProposalCreated(string price, string description, address proposer);

    /// @notice A proposal has been voted up
    /// @dev Notifies that a proposal has been voted up
    /// @param voter Address of the voter
    /// @param votes Total amount of votees cast
    event VotedUp(address voter, uint16 votes);

    /// @notice A proposal has been voted down
    /// @dev Notifies that a proposal has been voted down
    /// @param voter Address of the voter
    /// @param votes Total amount of votees cast
    event VotedDown(address voter, uint16 votes);

    /// @notice A proposal has been denied
    /// @param _for Total amount of votes cast in favour
    /// @param _against Total amount of votes cast against
    event ProposalDenied(uint16 _for, uint16 _against);

    /// @notice A proposal has been passed
    /// @param _for Total amount of votes cast in favour
    /// @param _against Total amount of votes cast against
    /// @param newPrice The newly updated price
    event ProposalPassed(uint16 _for, uint16 _against, string newPrice);

    /// @notice Indicates that a proposer has been rewarded
    /// @param addr Address of the proposer
    /// @param amount Amount sent to the proposer
    event RewardedProposer(address addr, uint256 amount);

    error NoActiveProposal();
    error NotEligible();
    error AlreadyVoted();
    error ProposalIsStillActive();
    error ProposalHasExpired();
    error ProposalAlreadyFinalized();
}

